import React, { useState, useEffect } from 'react';
import { Line } from 'react-chartjs-2';
import { Chart, CategoryScale, LinearScale, PointElement, LineElement, Tooltip } from 'chart.js';
import './styles.css';

Chart.register(CategoryScale, LinearScale, PointElement, LineElement, Tooltip);

const App = () => {
  const [weatherData, setWeatherData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedField, setSelectedField] = useState(null);

  const fetchData = async () => {
    const response = await fetch('http://159.65.154.81:80/data');
    const data = await response.json();
    setWeatherData(data);
    setLoading(false);
  };

  useEffect(() => {
    fetchData();
  }, []);

  const handleBoxClick = (field) => {
    setSelectedField(field);
  };

  const data = {
    labels: weatherData.map((item) => new Date(item.time).toLocaleTimeString()),
    datasets: [
      {
        label: selectedField || 'Temperature',
        data: weatherData.map((item) => item[selectedField || 'temperature']),
        borderColor: 'rgba(75, 192, 192, 1)',
        backgroundColor: 'rgba(75, 192, 192, 0.2)',
      },
      {
        label: 'HIC',
        data: weatherData.map((item) => item.hic),
        borderColor: 'rgba(255, 99, 132, 1)',
        backgroundColor: 'rgba(255, 99, 132, 0.2)',
      },
      {
        label: 'Raining',
        data: weatherData.map((item) => item.raining? 1 : 0),
        borderColor: 'rgba(255, 206, 86, 1)',
        backgroundColor: 'rgba(255, 206, 86, 0.2)',
      },
    ],
  };

  const options = {
    plugins: {
      categoryPercentage: {
        backgroundColor: (context) => {
          if (context.datasetIndex === 0) {
            return 'rgba(75, 192, 192, 0.4)';
          }
        },
      },
    },
    scales: {
      x: {
        title: {
          display: true,
          text: 'Time',
        },
      },
      y: {
        beginAtZero: true,
        title: {
          display: true,
          text: 'Value',
        },
      },
    },
    tooltip: {
      callbacks: {
        label: (context) => context.dataset.label + ': ' + context.parsed.y, // Display value on hover
      },
    },
  };

  return (
    <div>
      <h1>Weather Data</h1>
      {loading? (
        <p>Loading...</p>
      ) : (
        <div>
          <div className="box-container">
            <div
              className={`box ${selectedField === 'temperature'? 'selected' : ''}`}
              onClick={() => handleBoxClick('temperature')}
            >
              <div className="value-container">
                <p style={{ fontWeight: selectedField === 'temperature'? 'bold' : 'normal' }}>Temperature</p>
                <p>{weatherData[weatherData.length - 1].temperature}</p>
              </div>
            </div>
            <div
              className={`box ${selectedField === 'humidity'? 'selected' : ''}`}
              onClick={() => handleBoxClick('humidity')}
            >
              <div className="value-container">
                <p style={{ fontWeight: selectedField === 'humidity'? 'bold' : 'normal' }}>Humidity</p>
                <p>{weatherData[weatherData.length - 1].humidity}</p>
              </div>
            </div>
            <div
              className={`box ${selectedField === 'aqi'? 'selected' : ''}`}
              onClick={() => handleBoxClick('aqi')}
            >
              <div className="value-container">
                <p style={{ fontWeight: selectedField === 'aqi'? 'bold' : 'normal' }}>AQI</p>
                <p>{weatherData[weatherData.length - 1].aqi}</p>
              </div>
            </div>
            <div
              className={`box ${selectedField === 'time'? 'selected' : ''}`}
              onClick={() => handleBoxClick('time')}
            >
              <div className="value-container">
                <p style={{ fontWeight: selectedField === 'time'? 'bold' : 'normal' }}>Time (Last Updated)</p>
                <p>{weatherData[weatherData.length - 1].time}</p>
              </div>
            </div>
            <div
              className={`box ${selectedField === 'wifiStrength'? 'selected' : ''}`}
              onClick={() => handleBoxClick('wifiStrength')}
            >
              <div className="value-container">
                <p style={{ fontWeight: selectedField === 'wifiStrength'? 'bold' : 'normal' }}>Wifi Strength</p>
                <p>{weatherData[weatherData.length - 1].wifiStrength}</p>
              </div>
            </div>
            <div
              className={`box ${selectedField === 'hic'? 'selected' : ''}`}
              onClick={() => handleBoxClick('hic')}
            >
              <div className="value-container">
                <p style={{ fontWeight: selectedField === 'hic'? 'bold' : 'normal' }}>HIC</p>
                <p>{weatherData[weatherData.length - 1].hic}</p>
              </div>
            </div>
            <div
              className={`box ${selectedField === 'raining'? 'selected' : ''}`}
              onClick={() => handleBoxClick('raining')}
            >
              <div className="value-container">
                <p style={{ fontWeight: selectedField === 'raining'? 'bold' : 'normal' }}>Raining</p>
                <p>{weatherData[weatherData.length - 1].raining? 'Yes' : 'No'}</p>
              </div>
            </div>
          </div>
          <div className="chart-container">
            {selectedField && (
              <Line data={data} options={options} />
            )}
          </div>
        </div>
      )}
    </div>
  );
};

const boxStyles = {
  border: '2px solid #000',
  borderRadius: '5px',
  padding: '10px',
  margin: '10px',
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
  cursor: 'pointer',
  transition: 'font-weight 0.3s ease',
};

const selectedBoxStyles = {
 ...boxStyles,
  backgroundColor: '#f5f5f5',
};

const valueContainerStyles = {
  border: '1px solid #ccc',
  borderRadius: '5px',
  padding: '5px',
  margin: '5px',
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
};

export default App;
