import React, { useState, useEffect } from 'react';
import { Line } from 'react-chartjs-2';
import { Chart, CategoryScale, LinearScale, PointElement, LineElement } from 'chart.js';

Chart.register(CategoryScale, LinearScale, PointElement, LineElement);

const App = () => {
  const [weatherData, setWeatherData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedField, setSelectedField] = useState(null);

  const fetchData = async () => {
    const response = await fetch('http://159.65.154.81:80/data');
    const data = await response.json();
    setWeatherData(data);
    setLoading(false);
  };

  useEffect(() => {
    fetchData();
  }, []);

  const handleRowClick = (field) => {
    setSelectedField(field);
  };

  const data = {
    labels: weatherData.map((item) => new Date(item.time).toLocaleTimeString()),
    datasets: [
      {
        label: selectedField?.label || 'Temperature',
        data: weatherData.map((item) => item[selectedField?.field || 'temperature']),
        borderColor: 'rgba(75, 192, 192, 1)',
        backgroundColor: 'rgba(75, 192, 192, 0.2)',
      },
    ],
  };

  const options = {
    plugins: {
      categoryPercentage: {
        backgroundColor: (context) => {
          if (context.datasetIndex === 0) {
            return 'rgba(75, 192, 192, 0.4)';
          }
        },
      },
    },
    scales: {
      x: {
        title: {
          display: true,
          text: 'Time',
        },
      },
      y: {
        beginAtZero: true,
        title: {
          display: true,
          text: 'Value',
        },
      },
    },
  };

  return (
    <div>
      <h1>Weather Data</h1>
      {loading? (
        <p>Loading...</p>
      ) : (
        <div>
          <table>
            <thead>
              <tr>
                <th>Time</th>
                <th onClick={() => handleRowClick({label: 'Temperature', field: 'temperature'})}>Temperature</th>
                <th onClick={() => handleRowClick({label: 'Humidity', field: 'humidity'})}>Humidity</th>
                <th onClick={() => handleRowClick({label: 'Heat Index', field: 'hi'})}>Heat Index</th>
                <th onClick={() => handleRowClick({label: 'Raining', field: 'raining'})}>Raining</th>
                <th onClick={() => handleRowClick({label: 'WiFi Strength', field: 'wifiStrength'})}>WiFi Strength</th>
              </tr>
            </thead>
            <tbody>
              {weatherData.map((item, index) => (
                <tr key={index}>
                  <td>{new Date(item.time).toLocaleTimeString()}</td>
                  <td>{item.temperature}</td>
                  <td>{item.humidity}</td>
                  <td>{item.hi}</td>
                  <td>{item.raining? 'Yes' : 'No'}</td>
                  <td>{item.wifiStrength}</td>
                </tr>
              ))}
            </tbody>
          </table>
          {selectedField && (
            <Line data={data} options={options} />
          )}
        </div>
      )}
    </div>
  );
};

export default App;
