import React, { useState, useEffect } from 'react';
import { Line } from 'react-chartjs-2';
import { Chart, CategoryScale, LinearScale, PointElement, LineElement } from 'chart.js';

Chart.register(CategoryScale, LinearScale, PointElement, LineElement);

const App = () => {
  const [weatherData, setWeatherData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedField, setSelectedField] = useState(null);

  const fetchData = async () => {
    const response = await fetch('http://159.65.154.81:80/data');
    const data = await response.json();
    setWeatherData(data);
    setLoading(false);
  };

  useEffect(() => {
    fetchData();
  }, []);

  const handleRowClick = (field) => {
    setSelectedField(field);
  };

  const data = selectedField
    ? {
        labels: weatherData.map((item) => new Date(item.time).toLocaleTimeString()),
        datasets: [
          {
            label: selectedField,
            data: weatherData.map((item) => item[selectedField]),
            borderColor: 'rgba(75, 192, 192, 1)',
            backgroundColor: 'rgba(75, 192, 192, 0.2)',
          },
        ],
      }
    : null; // Hide the chart if no field is selected

  const options = {
    plugins: {
      categoryPercentage: {
        backgroundColor: (context) => {
          if (context.datasetIndex === 0) {
            return 'rgba(75, 192, 192, 0.4)';
          } else if (context.datasetIndex === 1) {
            return 'rgba(255, 99, 132, 0.4)';
          } else if (context.datasetIndex === 2) {
            return 'rgba(153, 102, 255, 0.4)';
          } else if (context.datasetIndex === 3) {
            return 'rgba(255, 206, 86, 0.4)';
          } else if (context.datasetIndex === 4) {
            return 'rgba(75, 192, 192, 0.4)';
          }
        },
      },
    },
    scales: {
      x: {
        title: {
          display: true,
          text: 'Time',
        },
      },
      y: {
        beginAtZero: true,
        title: {
          display: true,
          text: 'Value',
        },
      },
    },
  };

  return (
    <div>
      <h1>Weather Data</h1>
      {loading ? (
        <p>Loading...</p>
      ) : (
        <>
          <table>
            <thead>
              <tr>
                <th>Field</th>
                <th>Latest Value</th>
              </tr>
            </thead>
            <tbody>
              {weatherData.length > 0 && ( // Check for data before rendering table
                weatherData.map((item) => (
                  <tr key={item.time} onClick={() => handleRowClick(Object.keys(item)[0])}>
                    <td>{Object.keys(item)[0]}</td>
                    <td>{item[Object.keys(item)[0]]}</td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
          {selectedField && <Line data={data} options={options} />}
        </>
      )}
    </div>
  );
};

export default App;
