import axios from 'axios';
import React, { useState, useEffect } from 'react';
import { Line } from 'react-chartjs-2';
import 'chartjs-plugin-category-percentage'
import { CategoryPercentage } from 'chartjs-plugin-category-percentage';

const App = () => {
  const [weatherData, setWeatherData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      const result = await axios.get('http://159.65.154.81:80/data');
      setWeatherData(result.data);
      setLoading(false);
    };

    fetchData();
  }, []);

  const handleBoxClick = (field) => {
    console.log('Field clicked:', field);
  };

  const data = {
    labels: weatherData.map((item) => new Date(item.time).toLocaleTimeString()),
    datasets: [
      {
        label: 'Temperature',
        data: weatherData.map((item) => item.temperature),
        borderColor: 'rgba(75, 192, 192, 1)',
        backgroundColor: 'rgba(75, 192, 192, 0.2)',
      },
      {
        label: 'Humidity',
        data: weatherData.map((item) => item.humidity),
        borderColor: 'rgba(255, 99, 132, 1)',
        backgroundColor: 'rgba(255, 99, 132, 0.2)',
      },
      {
        label: 'Heat Index',
        data: weatherData.map((item) => item.hi),
        borderColor: 'rgba(153, 102, 255, 1)',
        backgroundColor: 'rgba(153, 102, 255, 0.2)',
      },
      {
        label: 'Raining',
        data: weatherData.map((item) => (item.raining ? 1 : 0)),
        borderColor: 'rgba(255, 206, 86, 1)',
        backgroundColor: 'rgba(255, 206, 86, 0.2)',
      },
      {
        label: 'WiFi Strength',
        data: weatherData.map((item) => item.wifiStrength),
        borderColor: 'rgba(75, 192, 192, 1)',
        backgroundColor: 'rgba(75, 192, 192, 0.2)',
      },
    ],
  };

  const options = {
    plugins: {
      categoryPercentage: {
        backgroundColor: 'rgba(255, 255, 255, 0.1)',
        precision: 2,
      },
    },
    scales: {
      x: {
        grid: {
          display: false,
        },
      },
      y: {
        grid: {
          display: false,
        },
        ticks: {
          precision: 0,
        },
      },
    },
  };

  return (
    <div>
      {loading ? (
        <p>Loading...</p>
      ) : (
        <div>
          <div
            style={{
              width: '200px',
              height: '200px',
              border: '1px solid black',
              margin: '10px',
              padding: '10px',
              boxSizing: 'border-box',
            }}
            onClick={() => handleBoxClick('temperature')}
          >
            <h2>Temperature</h2>
            <p>{weatherData.length > 0 ? weatherData[weatherData.length - 1].temperature : 'N/A'}</p>
            <p>{weatherData.length > 0 ? weatherData[weatherData.length - 1].humidity : 'N/A'}</p>
            <p>{weatherData.length > 0 ? weatherData[weatherData.length - 1].hi : 'N/A'}</p>
            <p>{weatherData.length > 0 ? weatherData[weatherData.length - 1].wifiStrength : 'N/A'}</p>
          </div>
          <Line data={data} options={options} />
        </div>
      )}
    </div>
  );
};

export default App;
