import React, { useState, useEffect } from 'react';
import { Line } from 'react-chartjs-2';
import { Chart, CategoryScale } from 'chart.js';

Chart.register(CategoryScale);
const App = () => {
  const [weatherData, setWeatherData] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchData = async () => {
    const response = await fetch('http://159.65.154.81:80/data');
    const data = await response.json();
    setWeatherData(data);
    setLoading(false);
  };

  useEffect(() => {
    fetchData();
  }, []);

  const handleBoxClick = (field) => {
    if (field === 'temperature') {
      alert('Field clicked: Temperature\nLatest Temperature: ' + weatherData[weatherData.length - 1].temperature);
    } else if (field === 'humidity') {
      alert('Field clicked: Humidity\nLatest Humidity: ' + weatherData[weatherData.length - 1].humidity);
    } else if (field === 'heatIndex') {
      alert('Field clicked: Heat Index\nLatest Heat Index: ' + weatherData[weatherData.length - 1].hi);
    } else if (field === 'raining') {
      alert('Field clicked: Raining\nLatest Raining Status: ' + (weatherData[weatherData.length - 1].raining ? 'Yes' : 'No'));
    } else if (field === 'wifiStrength') {
      alert('Field clicked: WiFi Strength\nLatest WiFi Strength: ' + weatherData[weatherData.length - 1].wifiStrength);
    }
  };

  const data = {
    labels: weatherData.map((item) => new Date(item.time).toLocaleTimeString()),
    datasets: [
      {
        label: 'Temperature',
        data: weatherData.map((item) => item.temperature),
        borderColor: 'rgba(75, 192, 192, 1)',
        backgroundColor: 'rgba(75, 192, 192, 0.2)',
      },
      {
        label: 'Humidity',
        data: weatherData.map((item) => item.humidity),
        borderColor: 'rgba(255, 99, 132, 1)',
        backgroundColor: 'rgba(255, 99, 132, 0.2)',
      },
      {
        label: 'Heat Index',
        data: weatherData.map((item) => item.hi),
        borderColor: 'rgba(153, 102, 255, 1)',
        backgroundColor: 'rgba(153, 102, 255, 0.2)',
      },
      {
        label: 'Raining',
        data: weatherData.map((item) => (item.raining ? 1 : 0)),
        borderColor: 'rgba(255, 206, 86, 1)',
        backgroundColor: 'rgba(255, 206, 86, 0.2)',
      },
      {
        label: 'WiFi Strength',
        data: weatherData.map((item) => item.wifiStrength),
        borderColor: 'rgba(75, 192, 192, 1)',
        backgroundColor: 'rgba(75, 192, 192, 0.2)',
      },
    ],
  };

  const options = {
    plugins: {
      categoryPercentage: {
        backgroundColor: (context) => {
          if (context.datasetIndex === 0) {
            return 'rgba(75, 192, 192, 0.4)';
          } else if (context.datasetIndex === 1) {
            return 'rgba(255, 99, 132, 0.4)';
          } else if (context.datasetIndex === 2) {
            return 'rgba(153, 102, 255, 0.4)';
          } else if (context.datasetIndex === 3) {
            return 'rgba(255, 206, 86, 0.4)';
          } else if (context.datasetIndex === 4) {
            return 'rgba(75, 192, 192, 0.4)';
          }
        },
      },
    },
    scales: {
      x: {
        title: {
          display: true,
          text: 'Time',
        },
      },
      y: {
        beginAtZero: true,
        title: {
          display: true,
          text: 'Value',
        },
      },
    },
  };

  return (
    <div>
      <h1>Weather Data</h1>
      {loading ? (
        <p>Loading...</p>
      ) : (
        <Line data={data} options={options} />
      )}
      <div>
        <button onClick={() => handleBoxClick('temperature')}>Temperature</button>
        <button onClick={() => handleBoxClick('humidity')}>Humidity</button>
        <button onClick={() => handleBoxClick('heatIndex')}>Heat Index</button>
        <button onClick={() => handleBoxClick('raining')}>Raining</button>
        <button onClick={() => handleBoxClick('wifiStrength')}>WiFi Strength</button>
      </div>
    </div>
  );
};

export default App;
